    if (UIAccessibilityIsVoiceOverRunning()) {
        self.isAccessibilityOn = YES;
    } else {
        self.isAccessibilityOn = NO;
    }
    if(self.isAccessibilityOn) {
        self.expanded = YES;
    }
    if ([IPFFeatureToggle isPaymentNextPaymentsEnabled]) {
        if (self.expanded) {
            self.nextPaymentsTableView.alpha = 0;
            self.nextPaymentsParentView.alpha = 0;
            self.transferViewHeightConstraint.constant += self.loadingView.frame.size.height + self.tableViewBottomConstraint.constant;
