                    self.nextPaymentsParentView.alpha = 1;
                    self.nextPaymentsTableView.alpha = 1;
                } completion:^(BOOL finished) {
                    [self.activityIndicator stopAnimating];
                    [self calculateTableRows];
                    [self setNewHeightForTransferDashboard];
                    [self adjustLayoutWithAnimation];
                }];
            }];
        } else {
            [self.activityIndicator stopAnimating];
        }
    }
    [self setNewHeightForTransferDashboard];
    [[NSNotificationCenter defaultCenter] addObserver:self selector:@selector(becameActive) name:kApplicationDidBecomeActiveNotification object:nil];
    [IPFPushHelper refreshNotificationCount];

    [IPFAnalyticsManager ADBTrackerTrackViewName:kAdobeAnalyticsNavigationTransactions
                             withContextPageName:kAdobeAnalyticsNavigationTransactions
                           withContextAppSection:kSectionHomeLoggedAdobeAnalytics
                          withContextAnalyticsId:[IPFEnvironmentUtil adobeAnalyticsID]
                           withContextIsLoggedIn: kLoginStateLoggedAdobeAnalytics
                                    completeWith:nil];
}

{
    CGFloat leftColumnHeight = kLeftColumnHeight;
    CGFloat rightColumnHeight = kRightColumnHeight;

    [self setupCellphoneRechargeVisibility:&rightColumnHeight];
    [self setupDDAEnabledVisibility:&rightColumnHeight];

    [self setupAuthorizedDebitVisibility:&leftColumnHeight];
    [self setupNextPaymentVisibility];

    if (leftColumnHeight >= rightColumnHeight) {
        self.contentViewHeightConstraint.constant = leftColumnHeight;
    } else {
        self.contentViewHeightConstraint.constant = rightColumnHeight;
    }
}

{
    if ([IPFPushHelper shouldEnableItauNotificationKit]) {
        UIButton *notificationButton = [IPFPushHelper injectNotificationButton:[self.view viewWithTag:kHeaderSecondaryButtonTag]];
        UITapGestureRecognizer *notificationButtonGesture = [[UITapGestureRecognizer alloc] initWithTarget:self action:@selector(showNotificationCenter)];
        notificationButtonGesture.numberOfTapsRequired = 1;
        [notificationButton setUserInteractionEnabled:YES];
        [notificationButton addGestureRecognizer:notificationButtonGesture];
    }
}

{

    [super viewWillDisappear:animated];
    [[IPFSessionManager sharedManager] updateUserPreference:NextPaymentsVisibilityPreference to:NO];
    if (self.expanded) {
        [self.nextPaymentHeader triggerExpandCollapseTable];
    }
    [self.tooltip dismissTooltipAnimated:NO];
    [[NSNotificationCenter defaultCenter] removeObserver:self name:kApplicationDidBecomeActiveNotification object:nil];
}

{
    NSDate *startDate = [NSDate date];
    NSDate *endDate = [startDate addDays:kNextPaymentsPeriod];
    __block typeof (self) weakSelf = self;
    [weakSelf.paymentTimelineManager fetchNextPaymentsUnpaidWithUser:[[IPFSessionManager sharedManager] sessionUser]
                                                           startDate:startDate
                                                             endDate:endDate
                                                          completion:^(NSArray<IPFNextPayment *> *arrayPayments, NSError *error) {
                                                              if (arrayPayments && arrayPayments.count > 0) {
                                                                  IPFNextPayment *firstItem = arrayPayments.firstObject;
                                                                  weakSelf.nextPaymentArray = [weakSelf filterNextPaymentArray:arrayPayments byDate:firstItem.date];
                                                              }
                                                              [weakSelf calculateTableRows];
                                                              [weakSelf loadNextPaymentsView];
                                                              if (completion) {
                                                                  completion();
                                                              }
                                                          }];
}

{

    if (self.expanded == YES) {
        if (self.nextPaymentArray.count == 0) {
            self.tableRows = 2;
        } else {
            self.tableRows = 1 + self.nextPaymentArray.count;
        }
    } else {
        self.tableRows = 1;
    }
}

{

    if (self.nextPaymentArray.count > 0) {
        self.transferDashboardView.nextPaymentHeaderView.emptyStatus = NO;
        IPFNextPayment *nextPayment = self.nextPaymentArray.firstObject;
        self.transferDashboardView.nextPaymentHeaderView.detailLabel.text = nextPayment.formattedCustomDate;
    }
    else {
        self.transferDashboardView.nextPaymentHeaderView.emptyStatus = YES;
    }
    self.transferDashboardView.nextPaymentHeaderView.numberOfNextpayments = self.nextPaymentArray.count;
    [self.transferDashboardView.nextPaymentHeaderView setupAccessibility];
    [self setNewHeightForTransferDashboard];
    [self adjustLayoutWithAnimation];
}

{

    CGFloat headerHeight;
    CGFloat tableRowsHeight;
    CGFloat totalHeight;
    CGRect dashboardFrame = self.transferDashboardView.frame;
    if (self.view.layer.frame.size.width-kCardMarginsSize > kTableViewNormalWidth) {
        headerHeight = kHeaderLargeHeight;
    } else {
        headerHeight = kHeaderNormalHeight;
    }
    if ([IPFFeatureToggle isPaymentNextPaymentsEnabled]) {
        self.nextPaymentsParentView.hidden = NO;
        if (self.nextPaymentArray.count == 0) {
            tableRowsHeight = kErrorCellHeight;
        } else {
            tableRowsHeight = self.nextPaymentArray.count * kHeightForRow;
        }
        self.nextPaymentsParentViewHeightConstraint.constant = headerHeight;
        if ([IPFFeatureToggle isTransactionCalendarEnabled]) {
            self.nextPaymentsParentViewHeightConstraint.constant += kNextPaymentsFooterViewHeight;
        }
        if (self.expanded) {
            self.nextPaymentsParentViewHeightConstraint.constant += tableRowsHeight;
        }
        totalHeight = kTransferViewHeightWithoutTable + self.nextPaymentsParentViewHeightConstraint.constant;
    }
    else {
        self.nextPaymentsParentView.hidden = YES;
        totalHeight = kTransferViewHeightWithoutTable;
    }
    self.transferViewHeightConstraint.constant = totalHeight + 16;
    [self.transferDashboardView setFrame:dashboardFrame];
}

{

    id cell;
    if (self.expanded && indexPath.row > 0) {
        if (self.nextPaymentArray.count == 0) {
            cell = [tableView dequeueReusableCellWithIdentifier:kNextPaymentErrorCellId];
        } else {
            IPFNextPayment *nextPayment = self.nextPaymentArray[indexPath.row - 1];
            cell = [tableView dequeueReusableCellWithIdentifier:[IPFNextPaymentCell cellIdentifier]];
            NSString *preffixAccessibility = [NSString stringWithFormat:NSLocalizedString(kLocalizedAccessibilityDashboardPaymentNextpaymentCellPreffixplaceholder, nil), indexPath.row + 1, self.nextPaymentArray.count];
            [cell setupCellWithPayment:nextPayment accessibilityString:preffixAccessibility];
        }
    } else {
        cell = [tableView dequeueReusableCellWithIdentifier:kNextPaymentHeaderCellId];
        self.nextPaymentHeader = (IPFNextPaymentHeaderTableViewCell *)cell;
        self.nextPaymentHeader.delegateListener = self;
        self.nextPaymentHeader.flipButton.hidden = self.isAccessibilityOn;
    }

    return cell;
}

{

    CGFloat rowHeight;
    if (indexPath.row == 0) {
        rowHeight = tableView.layer.frame.size.width-kCardMarginsSize > kTableViewNormalWidth ? kHeaderLargeHeight : kHeaderNormalHeight;
    } else {
        if (self.nextPaymentArray.count == 0) {
            rowHeight = kErrorCellHeight;
        } else {
            rowHeight = kHeightForRow;
        }
    }
    return rowHeight;
}

{

    if (self.expanded == YES) {
        [[IPFSessionManager sharedManager] updateUserPreference:NextPaymentsVisibilityPreference to:NO];
        self.expanded = NO;
        self.tableRows = 1;
        [self calculateTableRows];
        [flipButton setExpanded:self.expanded];
        [self setNewHeightForTransferDashboard];
        [self adjustLayoutWithAnimation];

    } else {
        [[IPFSessionManager sharedManager] updateUserPreference:NextPaymentsVisibilityPreference to:YES];
        self.expanded = YES;
        [UIView animateWithDuration:kAnimationDuration animations:^{
            flipButton.alpha = 0;
            activityIndicator.alpha = 1;
        } completion:^(BOOL finished) {
            [activityIndicator startAnimating];
            [self loadNextPaymentsWithCompletion:^ {
                [UIView animateWithDuration:kAnimationDuration animations:^{
                    flipButton.alpha = 1;
                    [flipButton setExpanded:self.expanded];
                    activityIndicator.alpha = 0;
                } completion:^(BOOL finished) {
                    [activityIndicator stopAnimating];
                    [self calculateTableRows];
                    [self setNewHeightForTransferDashboard];
                    [self adjustLayoutWithAnimation];
                }];
            }];
        }];
    }
}

{

    if ([IPFSessionManager sharedManager].currentAccountIsUnderContingency) {
        __weak __block typeof(IPFContingencyErrorViewController *)paymentWarningVC = (IPFContingencyErrorViewController *)[IPFStoryboardUtil contingencyPaymentWarningViewController];
        __weak __block typeof(self) weakSelf = self;
        paymentWarningVC.proceedToNextScreenBlock = ^void(void) {
            [paymentWarningVC dismissViewControllerAnimated:YES completion:^{
                [weakSelf handlePaymentClickAction];
            }];
        };
        [self presentViewController:paymentWarningVC animated:YES completion:nil];
    }
    else {
        [self handlePaymentClickAction];
    }
}

{

    if (UIAccessibilityIsVoiceOverRunning()) {
        [self openInputCodebarWithValue:@""];
    } else {
        __weak __block typeof(self) weakSelf = self;
        [IPFEnvironmentUtil requestCameraAccessWithCompletion:^(NSString *messageFailed, BOOL granted) {
            if (granted) {

                [IPFEnvironmentUtil trackEventWithCategory:kGACategoryDashboard action:kGAActionAbrirCamera label:nil value:nil];
                [weakSelf presentViewController:[IPFStoryboardUtil barcodeReaderNavigationControllerWithDelegate:weakSelf] animated:YES completion:nil];
            }
            else {
                if ([UIAlertController class]) {
                    UIAlertController *alert = [UIAlertController alertControllerWithTitle:nil message:messageFailed preferredStyle:UIAlertControllerStyleAlert];

                    UIAlertAction *inputCodebarAction = [UIAlertAction actionWithTitle:NSLocalizedString(kLocalizedDefaultLabelDigitecodigo, nil) style:UIAlertActionStyleDefault handler:^(UIAlertAction * _Nonnull action) {

                        [weakSelf openInputCodebarWithValue:@""];
                    }];
                    UIAlertAction *okAction = [UIAlertAction actionWithTitle:NSLocalizedString(kLocalizedButtonOk, nil) style:UIAlertActionStyleDefault handler:nil];

                    [alert addAction:inputCodebarAction];
                    [alert addAction:okAction];

                    [weakSelf presentViewController:alert animated:YES completion:nil];
                }
                else {
                    UIAlertView *alert = [[UIAlertView alloc] initWithTitle:messageFailed
                                                                    message:nil
                                                                   delegate:weakSelf
                                                          cancelButtonTitle:NSLocalizedString(kLocalizedButtonOk, nil) otherButtonTitles:NSLocalizedString(kLocalizedDefaultLabelDigitecodigo, nil), nil];
                    [alert show];
                    weakSelf.requestCameraAlertView = alert;
                }
            }
        }];
    }
}

{

    IPFMenuItemNew *item = [[IPFMenuItemNew alloc] init];
    if (type == DDA) {
        item.chaveMobile = kDDAOpKey;
        item.nome = NSLocalizedString(kLocalizedPaymentTitleDda, nil);
        [IPFEnvironmentUtil trackEventWithCategory:kGACategoryDashboard action:kGALabelDda label:nil value:nil];
    } else if (type == DAI) {
        item.chaveMobile = kDAIOpKey;
        item.nome = NSLocalizedString(kLocalizedPaymentTitleDai, nil);
        [IPFEnvironmentUtil trackEventWithCategory:kGACategoryDashboard action:kGALabelDai label:nil value:nil];
    }
    if (item.chaveMobile && item.chaveMobile.length > 0) {
        [[NSNotificationCenter defaultCenter] postNotificationName:kShowMenuItemNotification object:nil userInfo:@{ kMenuItemNotificationKey : item }];
    }
}

{
    if ([IPFFeatureToggle isPaymentCellphoneRechargeEnabled]) {
        [self.topUpPhoneView setHidden:NO];
        self.cellphoneRechargeTopSpaceConstraint.constant = kDefaultTopSpace;
        self.cellphoneRechargeHeightConstraint.constant = kCellphoneDdaHeightSpace;
        *rightColumnHeight += kRightColumnHeightCellphoneDda;
    } else {
        [self.topUpPhoneView setHidden:YES];
        self.cellphoneRechargeTopSpaceConstraint.constant = kZeroTopHeight;
        self.cellphoneRechargeHeightConstraint.constant = kZeroTopHeight;
    }
}

{
    if ([IPFFeatureToggle isPaymentDDAEnabled]) {
        [self.DDAView setHidden:NO];
        self.ddaTopSpaceConstraint.constant = kDefaultTopSpace;
        self.ddaHeightConstraint.constant = kCellphoneDdaHeightSpace;
        *rightColumnHeight += kRightColumnHeightCellphoneDda;

    } else {
        [self.DDAView setHidden:YES];
        self.ddaTopSpaceConstraint.constant = kZeroTopHeight;
        self.ddaHeightConstraint.constant = kZeroTopHeight;
    }
}

{
    if ([IPFFeatureToggle isPaymentAuthorizeDebitEnabled]) {
        [self.authorizeDebitView setHidden:NO];
        self.authorizeDebitTopSpaceConstraint.constant = kDefaultTopSpace;
        self.authorizeDebitHeightConstraint.constant = kAuthorizeDebitHeightConstraint;
        *leftColumnHeight += kLeftColumnHeightAuthorizedDebit;
    } else {
        [self.authorizeDebitView setHidden:YES];
        self.authorizeDebitTopSpaceConstraint.constant = kZeroTopHeight;
        self.authorizeDebitHeightConstraint.constant = kZeroTopHeight;
    }
}

{
    if ([IPFFeatureToggle isPaymentNextPaymentsEnabled]) {
        [self.nextPaymentsTableView setHidden:NO];
        [self.loadingView setHidden:NO];
    } else {
        [self.nextPaymentsTableView setHidden:YES];
        [self.loadingView setHidden:YES];
    }
}
